/* com.example.demo.mapper.AccountMapper.getAccountList */ SELECT ID , USER_ID , USER_PW , COALESCE(AGENCY, '') AS AGENCY , CASE ROLE WHEN 'ROLE_USER' THEN '유저' WHEN 'ROLE_MANAGER' THEN '매니저' WHEN 'ROLE_ADMIN' THEN '관리자' ELSE '유저' END AS ROLE , COALESCE(REMARK, '') AS REMARK , 0 AS SLOT_CNT FROM ACCOUNT AC WHERE 1=1
AND AC.ID = #{id}
AND AC.USER_ID LIKE '%' || #{userId} || '%'
AND AC.REMARK LIKE '%' || #{remark} || '%'
ORDER BY ID DESC LIMIT 10 OFFSET #{offset}

/* com.example.demo.mapper.AccountMapper.getAccountTotCnt */ SELECT COUNT(1) FROM ACCOUNT AC WHERE 1=1
AND AC.ID = #{id}
AND AC.USER_ID LIKE '%' || #{userId} || '%'
